// Create dyslexia font swapper
const fontSwapLabel = document.createElement('label');
fontSwapLabel.style = 'user-select: none;';
fontSwapLabel.appendChild(document.createTextNode(`${lang.dyslexiafont}: `));
const fontSwapCheckbox = document.createElement('input');
fontSwapCheckbox.type = 'checkbox';
fontSwapLabel.appendChild(fontSwapCheckbox);

const target = document.querySelector('.rss + br');

target.insertAdjacentElement('afterend', document.createElement('br'));
target.insertAdjacentElement('afterend', fontSwapLabel);

if (localStorage.font_66728) {
    fontSwapCheckbox.checked = true;
}

const fontCheck = () => {
    if (fontSwapCheckbox.checked) {
        document.querySelector('html').classList.remove('normal-font');
        document.querySelector('html').classList.add('alt-font');
    } else {
        document.querySelector('html').classList.remove('alt-font');
        document.querySelector('html').classList.add('normal-font');
    }
}

fontCheck();

fontSwapCheckbox.addEventListener('change', () => {
    if (localStorage.font_66728)
        localStorage.font_66728 = 1;
    else
        localStorage.font_66728 = 0;

    fontCheck();
});
})();

